--
CREATE
DEFINER = 'root'@'localhost'
PROCEDURE SUPER_CONSULTAR_FACTURAS (IN isucid int, IN itexto varchar(50),
IN icodfact varchar(10))
BEGIN

  DECLARE vwhere varchar(255) DEFAULT ' true';
  DECLARE idcodfact int;

  SELECT
    t.tipofacid INTO idcodfact
  FROM tipofact t
  WHERE t.codigo = icodfact;

  SET vwhere = CONCAT(' true and f.sucid = ', isucid, ' and f.tipofacid = ', idcodfact);

  IF (COALESCE(itexto, '**') <> '**'
    OR itexto <> '') THEN
    SET vwhere = CONCAT(' f.sucid = ', isucid, ' and f.tipofacid = ', idcodfact,
    " and CONCAT(f.codigo, t.nombre, t.codigo) LIKE '%", itexto, "%'");
  END IF;

  SET @query = CONCAT('SELECT * FROM factura f INNER JOIN tercero t ON f.terceroid = t.terceroid WHERE ', vwhere);

  PREPARE stmt FROM @query;
  EXECUTE stmt;

END
$$

